DROP DATABASE IF EXISTS battles;

CREATE DATABASE battles;

USE battles;

DROP TABLE IF EXISTS battles_data;

CREATE TABLE battles_data(
   name               VARCHAR(100) NOT NULL PRIMARY KEY
  ,year               INTEGER  
  ,battle_number      INTEGER  
  ,attacker_king      VARCHAR(50)
  ,defender_king      VARCHAR(50)
  ,attacker_1         VARCHAR(50) 
  ,attacker_2         VARCHAR(50)
  ,attacker_3         VARCHAR(50)
  ,attacker_4         VARCHAR(50)
  ,defender_1         VARCHAR(50)
  ,defender_2         VARCHAR(50)
  ,defender_3         VARCHAR(50)
  ,defender_4         VARCHAR(50)
  ,attacker_outcome   VARCHAR(4)
  ,battle_type        VARCHAR(50)
  ,major_death        BIT 
  ,major_capture      BIT 
  ,attacker_size      INTEGER 
  ,defender_size      INTEGER 
  ,attacker_commander VARCHAR(1000)
  ,defender_commander VARCHAR(1000)
  ,summer             BIT 
  ,location           VARCHAR(50)
  ,region             VARCHAR(50) 
  ,note               VARCHAR(1000)
);


select * from battles_data;
